CREATE OR REPLACE FUNCTION pls_obter_benef_regra_emissao ( nr_seq_regra_emissao_p bigint, nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
qt_registros_w		bigint;
nr_seq_plano_w		bigint;
nr_seq_contrato_w	bigint;


BEGIN

ds_retorno_w	:= 'N';

select	nr_seq_plano,
	nr_seq_contrato
into STRICT	nr_seq_plano_w,
	nr_seq_contrato_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

select	count(*)
into STRICT	qt_registros_w
from	pls_emissao_carteira_regra
where	nr_seq_regra	= nr_seq_regra_emissao_p
and		((coalesce(nr_seq_plano::text, '') = '')
or (nr_seq_plano	= nr_seq_plano_w))
and 	((coalesce(nr_seq_contrato::text, '') = '')
or (nr_seq_contrato = nr_seq_contrato_w));

if (qt_registros_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
