CREATE OR REPLACE FUNCTION pls_obter_bloqueio_autor (cd_material_p material.cd_material%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(50) := 'N';


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select	coalesce(max(y.ie_bloqueia_autor), 'N')
	into STRICT	ds_retorno_w
	from	pls_material_restricao	y,
		pls_material		x,
		material		a
	where	a.cd_material	= cd_material_p
	and	a.cd_material 	= x.cd_material
	and	x.nr_sequencia	= y.nr_seq_material
	and	clock_timestamp() between y.dt_inicio_vigencia and coalesce(y.dt_fim_vigencia, clock_timestamp());
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
