CREATE OR REPLACE FUNCTION pls_obter_boni_vinc_sca ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_bonificacao_w	varchar(4000);
nr_seq_plano_w		bigint;
nr_seq_bonificacao_w	bigint;

C01 CURSOR FOR
	SELECT	nr_seq_plano
	into STRICT	nr_seq_plano_w
	from	pls_sca_vinculo
	where	nr_seq_segurado	= nr_seq_segurado_p;

C02 CURSOR FOR
	SELECT	nr_seq_bonificacao
	into STRICT	nr_seq_bonificacao_w
	from	pls_bonificacao_vinculo
	where	nr_seq_plano	= nr_seq_plano_w;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_plano_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_seq_bonificacao_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	nm_bonificacao
		into STRICT	ds_bonificacao_w
		from	pls_bonificacao
		where	nr_sequencia	= nr_seq_bonificacao_w;

		ds_retorno_w		:= ds_bonificacao_w ||', '||ds_retorno_w;
		ds_bonificacao_w	:= '';
		end;
	end loop;
	close C02;

	end;
end loop;
close C01;


return	substr(ds_retorno_w,1,length(ds_retorno_w)-2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
