CREATE OR REPLACE FUNCTION pls_obter_bx_tit_rec_liq_mens ( nr_titulo_p bigint, nr_seq_baixa_p bigint, ie_tipo_item_p text, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_classif_w		double precision;
dt_liquidacao_w		timestamp;


BEGIN
select	coalesce(sum(a.vl_lancamento),0) vl_classif
into STRICT	vl_classif_w
from	pls_titulo_rec_liq_mens		a,
	pls_mensalidade_seg_item	b
where	b.nr_sequencia	= a.nr_seq_item_mens
and	((b.ie_tipo_item = ie_tipo_item_p) or (coalesce(ie_tipo_item_p::text, '') = ''))
and	a.nr_titulo	= nr_titulo_p
and	((a.nr_seq_baixa = nr_seq_baixa_p) or (coalesce(nr_seq_baixa_p::text, '') = ''))
and	a.dt_contabil		<= dt_referencia_p;

return vl_classif_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
