CREATE OR REPLACE FUNCTION pls_obter_camara_titulo (nr_titulo_pagar_p titulo_pagar.nr_titulo%type, nr_titulo_receber_p titulo_receber.nr_titulo%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) 	:= '';
nr_seq_lote_camara_w	pls_titulo_lote_camara.nr_seq_lote_camara%type;


BEGIN

if (nr_titulo_pagar_p IS NOT NULL AND nr_titulo_pagar_p::text <> '') then
	select	coalesce(max(nr_seq_lote_camara),0)
	into STRICT	nr_seq_lote_camara_w
	from	pls_titulo_lote_camara
	where	nr_titulo_pagar = nr_titulo_pagar_p;

	if (nr_seq_lote_camara_w > 0) then
		ds_retorno_w := wheb_mensagem_pck.get_Texto(318526) || nr_seq_lote_camara_w;
	end if;

elsif (nr_titulo_receber_p IS NOT NULL AND nr_titulo_receber_p::text <> '') then
	select	coalesce(max(nr_seq_lote_camara),0)
	into STRICT	nr_seq_lote_camara_w
	from	pls_titulo_lote_camara
	where	nr_titulo_receber = nr_titulo_receber_p;

	if (nr_seq_lote_camara_w > 0) then
		ds_retorno_w := wheb_mensagem_pck.get_Texto(318526) || nr_seq_lote_camara_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
