CREATE OR REPLACE FUNCTION pls_obter_carater_atend_analis ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nr_seq_execucao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_requisicao_w		bigint;


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	begin
		select	substr(obter_valor_dominio(2819,ie_carater_internacao),1,255)
		into STRICT	ds_retorno_w
		from	pls_guia_plano
		where	nr_sequencia	= nr_seq_guia_p;
	exception
	when others then
		ds_retorno_w	:= '';
	end;
elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	begin
		select	substr(obter_valor_dominio(2819,ie_carater_atendimento),1,255)
		into STRICT	ds_retorno_w
		from	pls_requisicao
		where	nr_sequencia	= nr_seq_requisicao_p;
	exception
	when others then
		ds_retorno_w	:= '';
	end;
elsif (nr_seq_execucao_p IS NOT NULL AND nr_seq_execucao_p::text <> '') then
	begin
		select	nr_seq_requisicao
		into STRICT	nr_seq_requisicao_w
		from	pls_execucao_requisicao
		where	nr_sequencia	= nr_seq_execucao_p;
	exception
	when others then
		nr_seq_requisicao_w	:= 0;
	end;

	if (coalesce(nr_seq_requisicao_w,0)	<> 0) then
		begin
			select	substr(obter_valor_dominio(2819,ie_carater_atendimento),1,255)
			into STRICT	ds_retorno_w
			from	pls_requisicao
			where	nr_sequencia	= nr_seq_requisicao_w;
		exception
		when others then
			ds_retorno_w	:= '';
		end;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
