CREATE OR REPLACE FUNCTION pls_obter_carteira_benef (nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Obter a carteira do beneficiário.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x] Tasy (Delphi/Java) [   ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_usuario_plano_w			varchar(255);


BEGIN
if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	begin
		select	max(a.nr_cartao_intercambio)
		into STRICT	cd_usuario_plano_w
		from	pls_segurado_carteira a,
			pls_segurado b
		where	b.nr_sequencia	= a.nr_seq_segurado
		and	b.nr_sequencia	= nr_seq_segurado_p
		and (a.dt_validade_carteira >= clock_timestamp()
		or 	coalesce(a.dt_validade_carteira::text, '') = '');
	exception
	when others then
		cd_usuario_plano_w	:= null;
	end;

	if (coalesce(cd_usuario_plano_w::text, '') = '') then
		begin
			select	max(cd_usuario_plano)
			into STRICT	cd_usuario_plano_w
			from	pls_segurado_carteira a,
				pls_segurado b
			where	a.nr_seq_segurado	= b.nr_sequencia
			and	b.nr_sequencia		= nr_seq_segurado_p;
		exception
		when others then
			cd_usuario_plano_w	:= null;
		end;
	end if;

	if (coalesce(cd_usuario_plano_w::text, '') = '') then
		begin
			select	max(a.nr_cartao_intercambio)
			into STRICT	cd_usuario_plano_w
			from	pls_segurado_carteira a,
				pls_segurado b
			where	b.nr_sequencia	= a.nr_seq_segurado
			and	b.nr_sequencia	= nr_seq_segurado_p;
		exception
		when others then
			cd_usuario_plano_w	:= null;
		end;
	end if;
end if;

return	cd_usuario_plano_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
