CREATE OR REPLACE FUNCTION pls_obter_carteira_cobranca (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


cd_carteira_w		varchar(10) := null;
nr_seq_conta_banco_w	bigint;


BEGIN
select	max(nr_seq_conta_banco)
into STRICT	nr_seq_conta_banco_w
from	titulo_receber a
where	a.nr_titulo	= nr_titulo_p;

/* Busca a carteira pelo título */

select	substr(max(b.cd_carteira),1,10)
into STRICT	cd_carteira_w
from	banco_carteira b,
	titulo_receber a
where	b.nr_sequencia  = a.nr_seq_carteira_cobr
and	a.nr_titulo 	= nr_titulo_p;

if (coalesce(cd_carteira_w::text, '') = '') then
	/* Senão, busca carteira do banco */

	select	max(a.cd_carteira)
	into STRICT	cd_carteira_w
	from	banco_estabelecimento a
	where	a.nr_sequencia	= nr_seq_conta_banco_w;
end if;

return	cd_carteira_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
