CREATE OR REPLACE FUNCTION pls_obter_cbos_medico ( cd_medico_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_cbo_w		varchar(255);
ds_cbo_ww		varchar(255);
			
C01 CURSOR FOR 
	SELECT	a.nr_seq_cbo_saude||' - '||b.ds_cbo 
	from	medico_especialidade a, 
		cbo_saude b 
	where	a.nr_seq_cbo_saude = b.nr_sequencia 
	and	a.cd_pessoa_fisica = cd_medico_p;
	

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	ds_cbo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	ds_cbo_ww||CASE WHEN ds_cbo_ww='' THEN ''  ELSE ', ' END ||ds_cbo_w 
	into STRICT	ds_cbo_ww 
	;
	end;
end loop;
close C01;
 
return	ds_cbo_ww;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
