CREATE OR REPLACE FUNCTION pls_obter_cbo_medico (cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_especialidade_w	bigint;


BEGIN

select  max(nr_seq_cbo_saude)
into STRICT	ds_retorno_w
from    medico_especialidade
where   cd_pessoa_fisica  = cd_pessoa_fisica_p
and     nr_seq_prioridade = (SELECT	min(nr_seq_prioridade)
                             from	medico_especialidade
                             where	cd_pessoa_fisica  = cd_pessoa_fisica_p
			     and	(nr_seq_cbo_saude IS NOT NULL AND nr_seq_cbo_saude::text <> ''));

if (coalesce(ds_retorno_w::text, '') = '' or ds_retorno_w = '') then

select	max(cd_especialidade)
into STRICT	cd_especialidade_w
from    medico_especialidade
where   cd_pessoa_fisica  = cd_pessoa_fisica_p
and     nr_seq_prioridade = (SELECT	min(nr_seq_prioridade)
                             from	medico_especialidade
                             where	cd_pessoa_fisica  = cd_pessoa_fisica_p
			     and	(cd_especialidade IS NOT NULL AND cd_especialidade::text <> ''));

	-- jjung - OS 513173 - 29/11/2012 - Tratado para obter o CBO cadastrado no Cadastro de Médicos com a menor prioridade.
	if ((cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '') or cd_especialidade_w = '') then
		begin
			/*aaschlote 11/11/2014 - Busca o CBO com a versão mais atualizada*/

			 select nr_seq_cbo_saude
			 into STRICT	ds_retorno_w
			 from (SELECT       nr_seq_cbo_saude
					    from    tiss_cbo_saude
					    where   cd_pessoa_fisica = cd_pessoa_fisica_p
					    and     cd_especialidade = cd_especialidade_w
					    order by ie_versao desc) alias2 LIMIT 1;
		exception
		when no_data_found then
			ds_retorno_w := null;
		end;
	end if;

	if (coalesce(ds_retorno_w::text, '') = '' or ds_retorno_w = '') then
		select	max(nr_seq_cbo_saude)
		into STRICT	ds_retorno_w
		from	especialidade_medica
		where	cd_especialidade	= cd_especialidade_w;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
