CREATE OR REPLACE FUNCTION pls_obter_cbo_prestador ( nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
	OPS - Análise de Contas Médicas
	OPS - Análise de Contas Médicas (Nova)
	OPS - Gestão de contas médicas
	OPS - Produção - Digitação de SP/SADT
	OPS - Produção - Digitação de Internações
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(255);
cd_especialidade_w		bigint;


BEGIN
--incluido o tratamento para buscar primeiramente pela especialidade principal e somente se não existir irá buscar por outra que não faça a restrição.
select	max(a.cd_especialidade)
into STRICT	cd_especialidade_w
from	pls_prestador_med_espec	a
where	a.nr_seq_prestador	= nr_seq_prestador_p
and	coalesce(ie_especialidade_princ,'S') = 'S'
and	clock_timestamp() between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp()), 'dd') and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()));

if (coalesce(cd_especialidade_w::text, '') = '') then
	select	max(a.cd_especialidade)
	into STRICT	cd_especialidade_w
	from	pls_prestador_med_espec	a
	where	a.nr_seq_prestador	= nr_seq_prestador_p
	and	clock_timestamp() between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp()), 'dd') and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()));
end if;

if (cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '') then
	select	max(a.nr_seq_cbo_saude)
	into STRICT	ds_retorno_w
	from	especialidade_medica	a
	where	a.cd_especialidade	= cd_especialidade_w;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_cbo_prestador ( nr_seq_prestador_p bigint) FROM PUBLIC;

