CREATE OR REPLACE FUNCTION pls_obter_cd_cooperativa (cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_cgc_w		varchar(14);
cd_cooperativa_w	varchar(10);


BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin

	select	max(cd_cgc_outorgante)
	into STRICT	cd_cgc_w
	from	pls_outorgante
	where	cd_estabelecimento = cd_estabelecimento_p;

	if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
		begin
		select	min(cd_cooperativa)
		into STRICT	cd_cooperativa_w
		from	pls_congenere
		where	cd_cgc = cd_cgc_w;
		end;
	end if;

	end;
end if;

return	cd_cooperativa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
