CREATE OR REPLACE FUNCTION pls_obter_cd_espec_med_prest ( cd_pessoa_fisica_p bigint, nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN
begin
select	a.cd_especialidade
into STRICT	ds_retorno_w
from	pls_prestador_med_espec	a
where	a.cd_pessoa_fisica		= cd_pessoa_fisica_p
and	a.ie_especialidade_princ	= 'S'
and	a.nr_seq_prestador		= nr_seq_prestador_p
and	clock_timestamp() between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp()), 'dd') and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()))
order by
	ie_rqe_espec desc LIMIT 1;
exception
when no_data_found then
	ds_retorno_w := null;
end;

return ds_retorno_w;

end;
