SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_cd_estab_ops () RETURNS ESTABELECIMENTO.CD_ESTABELECIMENTO%TYPE AS $body$
DECLARE


-- ATENÇÃO: Esta rotina atende a realidade dos clientes:
-- Tem mais de um estabelecimento:
-- (4) - Unimed Litoral
-- (30) - Marcio Cunha
-- (1) - Itamed, Hospital Costa Cavalcanti
-- Tem somente um estabelecimento
-- (1) - Unimed São José do Rio Preto
-- (52) - Unimed Blumenau
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN
if (coalesce(cd_estabelecimento_w::text, '') = '') then
	begin
	select	e.cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	pls_parametros	p,
		pls_outorgante	o,
		estabelecimento	e
	where	e.cd_estabelecimento = o.cd_estabelecimento
	and	p.cd_estabelecimento = o.cd_estabelecimento
	and	e.ie_ans             = 'S'
	and	(e.cd_ans IS NOT NULL AND e.cd_ans::text <> '');
	exception
	when others then
		cd_estabelecimento_w := null;
	end;

	if (coalesce(cd_estabelecimento_w::text, '') = '') then
		begin
		select	max(e.cd_estabelecimento)
		into STRICT	cd_estabelecimento_w
		from	pls_parametros	p,
			pls_outorgante	o,
			estabelecimento	e
		where	e.cd_estabelecimento = o.cd_estabelecimento
		and	p.cd_estabelecimento = o.cd_estabelecimento;
		exception
		when others then
			cd_estabelecimento_w := null;
		end;
	end if;
end if;

return	cd_estabelecimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
