SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_cd_guia_manual ( cd_seq_guia_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_tipo
  N - sequencia da guia
  M - guia manual
  C - cd guia
*/
ds_retorno_w			varchar(20);


BEGIN

if (ie_tipo_p = 'N') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_guia_plano
	where	cd_guia_manual = cd_seq_guia_p;

elsif (ie_tipo_p = 'M') then
	select	cd_guia_manual
	into STRICT	ds_retorno_w
	from	pls_guia_plano
	where	nr_sequencia = cd_seq_guia_p;
elsif (ie_tipo_p = 'C') then
	select	max(cd_guia)
	into STRICT	ds_retorno_w
	from	pls_guia_plano
	where	nr_sequencia = cd_seq_guia_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
