CREATE OR REPLACE FUNCTION pls_obter_cd_motivo_saida ( nr_seq_motivo_saida_p integer, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/*
IE_TIPO_P
S - SPSADT
I - INTERNADO
*/
ds_retorno_w    varchar(255);


BEGIN

if (ie_tipo_p = 'S') then

        select  max(cd_tiss)
        into STRICT    ds_retorno_w
        from    pls_motivo_saida_sadt
        where   nr_sequencia    = nr_seq_motivo_saida_p;

elsif (ie_tipo_p = 'I') then

        select  max(cd_tiss)
        into STRICT    ds_retorno_w
        from    pls_motivo_saida
        where   nr_sequencia    = nr_seq_motivo_saida_p;

end if;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
