CREATE OR REPLACE FUNCTION pls_obter_cd_seq_congenere ( cd_nr_referencia_p bigint, ie_tipo_retorno_p text) RETURNS bigint AS $body$
DECLARE


/*========= IE_TIPO_RETORNO_P ==========  */

/* 	CD - Código Cooperativa		  */

/* 	NR - Sequência Operadora Congênere */

/*========================================= */

/* Obs: cd_nr_referencia_p terá que conter o código de Cooperativa ou a sequência da Congênere. */

ds_retorno_w		bigint;


BEGIN

if (ie_tipo_retorno_p	= 'CD') then
	select	max(cd_cooperativa)
	into STRICT	ds_retorno_w
	from	pls_congenere
	where	nr_sequencia	= cd_nr_referencia_p;

elsif (ie_tipo_retorno_p	= 'NR') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_congenere
	where	cd_cooperativa	= cd_nr_referencia_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
