CREATE OR REPLACE FUNCTION pls_obter_cd_seq_guia ( nr_seq_guia_p bigint, cd_guia_p text) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter o código ou sequência da guia
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w			varchar(20);
nr_seq_guia_w			pls_guia_plano.nr_sequencia%type;


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	cd_guia
	into STRICT	ds_retorno_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_p;
elsif (cd_guia_p IS NOT NULL AND cd_guia_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_guia_w
	from	pls_guia_plano
	where	cd_guia	= cd_guia_p;

	ds_retorno_w := to_char(nr_seq_guia_w);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
