CREATE OR REPLACE FUNCTION pls_obter_censo_mes ( dt_mes_ref_p timestamp, ie_informacao_p text, cd_estabelecimento_p bigint, nr_seq_plano_p bigint, ie_segmentacao_p text, ie_tipo_contratacao_p text, nr_seq_faixa_etaria_p bigint, ie_regulamentacao_p text, nr_seq_vendedor_canal_p bigint, nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
qt_itens_w	bigint;


BEGIN

if (coalesce(cd_estabelecimento_p,0) <> 0) then		/* Estabelecimento */
	select	count(*)
	into STRICT	qt_itens_w
	from	w_pls_censo
	where	dt_mes_referencia = dt_mes_ref_p
	and	ie_situacao_beneficiario = ie_informacao_p
	and	cd_estabelecimento_p	= cd_estabelecimento;
elsif (coalesce(nr_seq_plano_p,0) <> 0) then		/* Plano */
	select	count(*)
	into STRICT	qt_itens_w
	from	w_pls_censo
	where	dt_mes_referencia = dt_mes_ref_p
	and	ie_situacao_beneficiario = ie_informacao_p
	and	nr_seq_plano_p	= nr_seq_plano;
elsif (coalesce(ie_segmentacao_p,'0') <> '0') then		/* Segmentação */
	select	count(*)
	into STRICT	qt_itens_w
	from	w_pls_censo
	where	dt_mes_referencia = dt_mes_ref_p
	and	ie_situacao_beneficiario = ie_informacao_p
	and	ie_segmentacao_p	= ie_segmentacao;
elsif (coalesce(ie_tipo_contratacao_p,'0') <> '0') then	/* Tipo contratação */
	select	count(*)
	into STRICT	qt_itens_w
	from	w_pls_censo
	where	dt_mes_referencia = dt_mes_ref_p
	and	ie_situacao_beneficiario = ie_informacao_p
	and	ie_tipo_contratacao_p	= ie_tipo_contratacao;
elsif (coalesce(nr_seq_faixa_etaria_p,0) <> 0) then	/* Faixa etária */
	select	count(*)
	into STRICT	qt_itens_w
	from	w_pls_censo
	where	dt_mes_referencia = dt_mes_ref_p
	and	ie_situacao_beneficiario = ie_informacao_p
	and	nr_seq_faixa_etaria_p	= nr_seq_faixa_etaria;
elsif (coalesce(ie_regulamentacao_p,'0') <> '0') then	/* Regulamentação */
	select	count(*)
	into STRICT	qt_itens_w
	from	w_pls_censo
	where	dt_mes_referencia = dt_mes_ref_p
	and	ie_situacao_beneficiario = ie_informacao_p
	and	ie_regulamentacao_p	= ie_regulamentacao;
elsif (coalesce(nr_seq_vendedor_canal_p,0) <> 0) then	/* Vendedor */
	select	count(*)
	into STRICT	qt_itens_w
	from	w_pls_censo
	where	dt_mes_referencia = dt_mes_ref_p
	and	ie_situacao_beneficiario = ie_informacao_p
	and	nr_seq_vendedor_canal_p	= nr_seq_vendedor_canal;
elsif (coalesce(nr_seq_contrato_p,'0') <> '0') then	/* Contrato */
	select	count(*)
	into STRICT	qt_itens_w
	from	w_pls_censo
	where	dt_mes_referencia = dt_mes_ref_p
	and	ie_situacao_beneficiario = ie_informacao_p
	and	nr_seq_contrato_p	= nr_seq_contrato;
end if;

ds_retorno_w := to_char(qt_itens_w);

return	ds_retorno_w;

end;
