CREATE OR REPLACE FUNCTION pls_obter_cgc_intercambio ( nr_seq_intercambio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_intercambio_w	bigint;

BEGIN
select	a.nr_sequencia,
	(select	substr(x.nm_pessoa_fisica,1,255)
	from	pessoa_fisica x
	where	x.cd_pessoa_fisica	= a.cd_pessoa_fisica
	
union all

	select	substr(x.ds_razao_social,1,255)
	from	pessoa_juridica x
	where	x.cd_cgc	= a.cd_cgc)
	--substr(obter_nome_pf_pj(cd_pessoa_fisica, cd_cgc),1,255)
into STRICT	nr_seq_intercambio_w,
	ds_retorno_w
from	pls_intercambio	a
where	a.nr_sequencia	= nr_seq_intercambio_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
