CREATE OR REPLACE FUNCTION pls_obter_chave_biometria_web ( nr_seq_prestador_p bigint, nr_seq_local_atend_p bigint, ds_endereco_mac_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(255) := null;
nr_seq_local_atend_w	bigint;


BEGIN
nr_seq_local_atend_w := nr_seq_local_atend_p;

if (ie_opcao_p = 'B') then
	if (coalesce(nr_seq_local_atend_w::text, '') = '') then
		select 	coalesce(max(a.nr_sequencia), 0)
		into STRICT	nr_seq_local_atend_w
		from	local_atendimento_medico a,
			local_atend_med_prest b
		where	a.nr_sequencia = b.nr_sequencia
		and	ie_situacao = 'A'
		and	b.nr_seq_prestador = nr_seq_prestador_p;
	end if;

	if (nr_seq_local_atend_w > 0) then

		if (ds_endereco_mac_p IS NOT NULL AND ds_endereco_mac_p::text <> '') then
			select	max(b.ds_chave_ativacao)
			into STRICT 	ds_retorno_w
			from	local_atend_med_maquina a,
				local_atend_med_maq_finger b
			where	a.nr_sequencia 		= b.nr_seq_maquina
			and	a.nr_seq_local_atend 	= nr_seq_local_atend_w
			and	a.ds_endereco_mac = ds_endereco_mac_p
			and	a.ie_situacao = 'A';
		end if;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	max(b.ds_chave_ativacao)
			into STRICT 	ds_retorno_w
			from	local_atend_med_maquina a,
				local_atend_med_maq_finger b
			where	a.nr_sequencia 		= b.nr_seq_maquina
			and	a.nr_seq_local_atend 	= nr_seq_local_atend_w
			and	a.ie_situacao = 'A';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
