SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_cid_classificacao ( nr_seq_guia_p bigint, ie_origem_p text) RETURNS varchar AS $body$
DECLARE

/*
  ie_origem
	A - Autorizações
*/
qt_cid_w			integer;
ds_retorno_w			varchar(1);


BEGIN
if (ie_origem_p = 'A') then
	select	count(*)
	into STRICT	qt_cid_w
	from	pls_diagnostico
	where	nr_seq_guia = nr_seq_guia_p;
end if;

if (qt_cid_w = 0) then
	ds_retorno_w := 'P';
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
