CREATE OR REPLACE FUNCTION pls_obter_cid_conta_guia ( nr_seq_conta_p bigint, nr_seq_guia_p bigint, cd_guia_p text, nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


cd_cid_w		varchar(10);
nr_seq_guia_w		bigint;
cd_guia_w		varchar(10);
nr_seq_segurado_w	bigint;


BEGIN

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') and (coalesce(nr_seq_guia_p::text, '') = '') then

	begin
		select	nr_seq_guia,
			cd_guia
		into STRICT	nr_seq_guia_w,
			cd_guia_w
		from	pls_conta
		where	nr_sequencia = nr_seq_conta_p;
	exception
	when no_data_found then
		nr_seq_guia_w := null;
		cd_guia_w := null;
	end;

end if;

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') and (coalesce(nr_seq_segurado_p::text, '') = '') then

	begin
		select	nr_seq_segurado
		into STRICT	nr_seq_segurado_w
		from	pls_conta
		where	nr_sequencia = nr_seq_conta_p;
	exception
	when no_data_found then
		nr_seq_segurado_w := null;
	end;
end if;

if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	nr_seq_segurado_w := nr_seq_segurado_p;
end if;

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	nr_seq_guia_w := nr_seq_guia_p;
end if;

if (cd_guia_p IS NOT NULL AND cd_guia_p::text <> '') then
	cd_guia_w := cd_guia_p;

	if (coalesce(nr_seq_guia_w::text, '') = '') then
		begin
			select	max(nr_sequencia)
			into STRICT	nr_seq_guia_w
			from	pls_guia_plano
			where	((cd_guia = cd_guia_w) or (cd_guia_imp = cd_guia_w))
			and	nr_seq_segurado = nr_seq_segurado_w
			and	ie_status = '1';
		exception
		when no_data_found then
			nr_seq_guia_w := null;
		end;
	end if;
end if;

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	begin
		select	coalesce(cd_doenca, cd_doenca_imp)
		into STRICT	cd_cid_w
		from	pls_diagnostico_conta
		where 	ie_classificacao = 'P'
		and	((cd_doenca IS NOT NULL AND cd_doenca::text <> '') or (cd_doenca_imp IS NOT NULL AND cd_doenca_imp::text <> ''))
		and	nr_seq_conta = nr_seq_conta_p
		order by coalesce(cd_doenca, cd_doenca_imp) LIMIT 1;
	exception
	when no_data_found then
		cd_cid_w := null;
	end;
end if;

if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') and (coalesce(cd_cid_w::text, '') = '') then

	begin
		select	coalesce(cd_doenca, cd_doenca_imp)
		into STRICT	cd_cid_w
		from	pls_diagnostico
		where 	ie_classificacao = 'P'
		and	((cd_doenca IS NOT NULL AND cd_doenca::text <> '') or (cd_doenca_imp IS NOT NULL AND cd_doenca_imp::text <> ''))
		and	nr_seq_guia = nr_seq_guia_w
		order by coalesce(cd_doenca, cd_doenca_imp) LIMIT 1;
	exception
	when no_data_found then
		cd_cid_w := null;
	end;
end if;

return	cd_cid_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
