CREATE OR REPLACE FUNCTION pls_obter_cid_diag_tiss ( nr_seq_lote_anexo_guia_p bigint, ie_ordenacao_p text) RETURNS varchar AS $body$
DECLARE

/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Esta function foi criada para obter os CID's (Principal, secundario, terceiro, quarto) vinculados à
Guia/requisição, para que seja apresentada esta informação no XML de retorno das transações do TISS
----------------------------------------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ] Relatórios [X] Outros: Gerenciador de XML
 ----------------------------------------------------------------------------------------------------------------------------------------------------

Pontos de atenção: 
ie_ordenacao_p:
P - Principal
S - Secundario
T - Terceiro
Q - Quarto
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/cd_cid_w		varchar(4);
qt_registro_cid_w	smallint := 1;

C01 CURSOR FOR
	SELECT	cd_doenca
	from	pls_lote_anexo_diag_aut
	where	nr_seq_lote_anexo_guia = nr_seq_lote_anexo_guia_p
	and	(cd_doenca IS NOT NULL AND cd_doenca::text <> '')  LIMIT 4;

BEGIN

if (nr_seq_lote_anexo_guia_p IS NOT NULL AND nr_seq_lote_anexo_guia_p::text <> '') then
	for r_c01_w in C01 loop
		if (ie_ordenacao_p = 'P' and qt_registro_cid_w = 1) then
			cd_cid_w := r_c01_w.cd_doenca;
			exit;
		elsif (ie_ordenacao_p = 'S' and qt_registro_cid_w = 2) then
			cd_cid_w := r_c01_w.cd_doenca;
			exit;
		elsif (ie_ordenacao_p = 'T' and qt_registro_cid_w = 3) then
			cd_cid_w := r_c01_w.cd_doenca;
			exit;
		elsif (ie_ordenacao_p = 'Q' and qt_registro_cid_w = 4) then
			cd_cid_w := r_c01_w.cd_doenca;
			exit;
		end if;

		qt_registro_cid_w := qt_registro_cid_w + 1;

	end loop;
end if;

return	cd_cid_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
