SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_cid_guia_relat ( nr_seq_guia_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p =
	 CID1 - CID principal
	 CID2 - CID secundário 1
	 CID3 - CID secundário 2
	 CID4 - CID secundário 3
*/
ds_retorno_w		varchar(255);


BEGIN
if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	if (ie_opcao_p = 'CID1') then
		select	max(cd_doenca)
		into STRICT	ds_retorno_w
		from	pls_diagnostico
		where	nr_seq_guia		= nr_seq_guia_p
		and	ie_classificacao 	= 'P'  LIMIT 1;
	elsif (ie_opcao_p = 'CID2') then
		select  max(a.cd_doenca)
		into STRICT	ds_retorno_w
		from    pls_diagnostico		a,
			pls_guia_plano		b
		where   a.nr_seq_guia		= b.nr_sequencia
		and     b.nr_sequencia		= nr_seq_guia_p
		and	a.ie_classificacao 	= 'S'
		order by a.nr_sequencia LIMIT 1;
	elsif (ie_opcao_p = 'CID3') then
		select  max(a.cd_doenca)
		into STRICT	ds_retorno_w
		from    pls_diagnostico		a,
			pls_guia_plano		b
		where   a.nr_seq_guia  		= b.nr_sequencia
		and     b.nr_sequencia		= nr_seq_guia_p
		and	a.ie_classificacao 	= 'S'
		and	a.nr_sequencia > (	SELECT  min(a.nr_sequencia)
						from    pls_diagnostico		a,
							pls_guia_plano		b
						where  	a.nr_seq_guia		= b.nr_sequencia
						and     b.nr_sequencia		= nr_seq_guia_p
						and	a.ie_classificacao 	= 'S');
	elsif (ie_opcao_p = 'CID4') then
		select  max(a.cd_doenca)
		into STRICT	ds_retorno_w
		from    pls_diagnostico		a,
			pls_guia_plano		b
		where   a.nr_seq_guia  		= b.nr_sequencia
		and     b.nr_sequencia		= nr_seq_guia_p
		and	a.ie_classificacao 	= 'S'
		and	a.nr_sequencia > (	SELECT  min(a.nr_sequencia)
						from    pls_diagnostico		a,
							pls_guia_plano		b
						where  	a.nr_seq_guia		= b.nr_sequencia
						and     b.nr_sequencia		= nr_seq_guia_p
						and	a.ie_classificacao 	= 'S'
						and	a.nr_sequencia > (	select	min(a.nr_sequencia)
										from   	pls_diagnostico		a,
											pls_guia_plano		b
										where   a.nr_seq_guia		= b.nr_sequencia
										and     b.nr_sequencia		= nr_seq_guia_p
										and	a.ie_classificacao 	= 'S'))
		order by a.nr_sequencia LIMIT 1;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
