SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_classif_conta ( nr_seq_conta_p bigint) RETURNS varchar AS $body$
DECLARE

/* Retorno
	1 - Ambulatorial
	2 - Internamento cirúrgico
	3 - Internamento clínico
	4 - Internamento obstétrico
	5 - Odontológico
	6 - Psiquiátrico
	7 - Internamento Psiquiátrico
*/
ds_retorno_w			varchar(2);
ie_internado_w			varchar(2);
nr_seq_clinica_w		bigint;
cd_tiss_w			varchar(20);


BEGIN

select	substr(pls_obter_se_internado(nr_sequencia,'C'),1,2),
	nr_seq_clinica
into STRICT	ie_internado_w,
	nr_seq_clinica_w
from	pls_conta
where	nr_sequencia	= nr_seq_conta_p;

if (ie_internado_w	= 'S') then
	select	cd_tiss
	into STRICT	cd_tiss_w
	from	pls_clinica
	where	nr_sequencia	= nr_seq_clinica_w;

	if (cd_tiss_w	= '1') then
		ds_retorno_w	:= '3';
	elsif (cd_tiss_w	= '2') then
		ds_retorno_w	:= '2';
	elsif (cd_tiss_w	= '3') then
		ds_retorno_w	:= '4';
	elsif (cd_tiss_w	= '5') then
		ds_retorno_w	:= '7';
	end if;
else
	ds_retorno_w	:= '1';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
