CREATE OR REPLACE FUNCTION pls_obter_classif_pel ( nr_seq_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';
dt_protocolo_w		timestamp;
dt_movimento_w		timestamp;
qt_dias_w		bigint;


BEGIN

select	a.dt_mes_competencia
into STRICT	dt_protocolo_w
from	pls_conta		b,
	pls_protocolo_conta	a
where	a.nr_sequencia = b.nr_seq_protocolo
and	b.nr_sequencia = nr_seq_conta_p;

select	max(dt_atualizacao_nrec)
into STRICT	dt_movimento_w
from	pls_conta
where	nr_sequencia	= nr_seq_conta_p;

/*
begin
select	max(a.dt_movimento)
into	dt_movimento_w
from	pls_conta_proc		d,
	movimento_contabil	a
where	a.cd_conta_contabil in (d.cd_conta_deb, d.cd_conta_cred, d.cd_conta_provisao_cred, d.cd_conta_provisao_deb, d.cd_conta_glosa_deb, d.cd_conta_glosa_cred)
and	a.cd_historico in (d.cd_historico, d.cd_historico_glosa, d.cd_historico_prov, d.cd_historico_rev_prov)
and	(a.nr_seq_agrupamento is null or a.nr_seq_agrupamento = d.nr_seq_conta)
and	d.nr_seq_conta 	= nr_seq_conta_p;
exception
when others then
	dt_movimento_w 	:= null;
end;

if	(dt_movimento_w is null) then
	begin
	select	max(a.dt_movimento)
	into	dt_movimento_w
	from	pls_conta_mat		d,
		movimento_contabil	a
	where	a.cd_conta_contabil in (d.cd_conta_deb, d.cd_conta_cred, d.cd_conta_provisao_cred, d.cd_conta_provisao_deb, d.cd_conta_glosa_deb, d.cd_conta_glosa_cred)
	and	a.cd_historico in (d.cd_historico, d.cd_historico_glosa, d.cd_historico_prov, d.cd_historico_rev_prov)
	and	(a.nr_seq_agrupamento is null or a.nr_seq_agrupamento = d.nr_seq_conta)
	and	d.nr_seq_conta 	= nr_seq_conta_p;
	exception
	when others then
		dt_movimento_w 	:= null;
	end;
end if;	*/
if (dt_protocolo_w IS NOT NULL AND dt_protocolo_w::text <> '') and (dt_movimento_w IS NOT NULL AND dt_movimento_w::text <> '') then

	qt_dias_w	:=  trunc(dt_protocolo_w,'dd') - trunc(dt_movimento_w,'dd');

	if (qt_dias_w >= 0) then
		if (qt_dias_w > 30) then
			ds_retorno_w := 'Avisado há mais de 30 dias';
		else
			ds_retorno_w := 'Avisado nos últimos 30 dias';
		end if;
	else
		ds_retorno_w := '';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
