SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_cnes_prestador (nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE

					
ds_retorno_w			varchar(255);
cd_cnes_w			varchar(255);
cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(14);
ie_tipo_endereco_w		varchar(5);
nr_seq_ident_cnes_w		bigint;
nr_seq_compl_pf_tel_adic_w	bigint;
nr_seq_compl_pj_w		bigint;


BEGIN
if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then
	select	a.ie_tipo_endereco,
		a.cd_pessoa_fisica,
		a.cd_cgc,
		a.nr_seq_compl_pf_tel_adic,
		nr_seq_compl_pj
	into STRICT	ie_tipo_endereco_w,
		cd_pessoa_fisica_w,
		cd_cgc_w,
		nr_seq_compl_pf_tel_adic_w,
		nr_seq_compl_pj_w
	from	pls_prestador a
	where	a.nr_sequencia	= nr_seq_prestador_p;
	
	if (ie_tipo_endereco_w in ('PJ','PJC','PJF','PJA')) then
		if (ie_tipo_endereco_w = 'PJ') then
			select	max(a.cd_cnes),
				max(a.nr_seq_ident_cnes)
			into STRICT	cd_cnes_w,
				nr_seq_ident_cnes_w
			from	pessoa_juridica a
			where	a.cd_cgc	= cd_cgc_w;
			
			if (nr_seq_ident_cnes_w IS NOT NULL AND nr_seq_ident_cnes_w::text <> '') then
				select	CASE WHEN coalesce(max(cd_cnes)::text, '') = '' THEN cd_cnes_w  ELSE max(cd_cnes) END
				into STRICT	ds_retorno_w
				from	cnes_identificacao
				where	nr_sequencia	= nr_seq_ident_cnes_w;
				
			elsif (cd_cnes_w IS NOT NULL AND cd_cnes_w::text <> '') then
				ds_retorno_w := cd_cnes_w;		
			end if;
		elsif (ie_tipo_endereco_w = 'PJC') then
			select	max(b.cd_cnes)
			into STRICT	ds_retorno_w
			from	cnes_identificacao	b,
				pessoa_juridica_compl	a
			where	b.nr_sequencia		= a.nr_seq_ident_cnes
			and	a.cd_cgc		= cd_cgc_w
			and	a.ie_tipo_complemento	= 1;
		elsif (ie_tipo_endereco_w = 'PJF') then
			select	max(b.cd_cnes)
			into STRICT	ds_retorno_w
			from	cnes_identificacao	b,
				pessoa_juridica_compl	a
			where	b.nr_sequencia		= a.nr_seq_ident_cnes
			and	a.cd_cgc		= cd_cgc_w
			and	a.ie_tipo_complemento	= 2;
		else
			select	max(b.cd_cnes)--obter_compl_pj(cd_cgc,ie_tipo_complemento,'CNES')
			into STRICT	ds_retorno_w
			from	cnes_identificacao	b,
				pessoa_juridica_compl	a
			where	a.nr_seq_ident_cnes	= b.nr_sequencia
			and	a.nr_sequencia		= nr_seq_compl_pj_w
			and	a.cd_cgc		= cd_cgc_w;
		end if;
	elsif (ie_tipo_endereco_w in ('PFR')) then
		select	max(a.cd_cnes)
		into STRICT	ds_retorno_w
		from	pessoa_fisica a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_w;
	elsif (ie_tipo_endereco_w in ('PFC')) then
		if (nr_seq_compl_pf_tel_adic_w IS NOT NULL AND nr_seq_compl_pf_tel_adic_w::text <> '') then
			select	max(a.nr_seq_ident_cnes)
			into STRICT	nr_seq_ident_cnes_w
			from	compl_pf_tel_adic a
			where	a.cd_pessoa_fisica	= cd_pessoa_fisica_w
			and	a.nr_sequencia		= nr_seq_compl_pf_tel_adic_w;
			
			if (nr_seq_ident_cnes_w IS NOT NULL AND nr_seq_ident_cnes_w::text <> '') then
				select	max(a.cd_cnes)
				into STRICT	ds_retorno_w
				from	cnes_identificacao a
				where	a.nr_sequencia	= nr_seq_ident_cnes_w;
			end if;
		else
			select	max(obter_compl_pf(cd_pessoa_fisica_w,2,'CNES'))
			into STRICT	ds_retorno_w
			;
		end if;	
	elsif (ie_tipo_endereco_w = 'PFC2') then
		select	max(obter_compl_pf(cd_pessoa_fisica_w,7,'CNES'))
		into STRICT	ds_retorno_w
		;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_cnes_prestador (nr_seq_prestador_p bigint) FROM PUBLIC;

