CREATE OR REPLACE FUNCTION pls_obter_codigo_material (cd_material_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_codigo_w	varchar(50)	:= null;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select	max(a.cd_material_ops)
	into STRICT	ds_codigo_w
	from	pls_material a
	where	a.cd_material	= cd_material_p
	and	dt_referencia_p between a.dt_inclusao and coalesce(a.dt_exclusao,dt_referencia_p)
	and	a.ie_situacao = 'A';
end if;

return	ds_codigo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
