CREATE OR REPLACE FUNCTION pls_obter_codigo_serv_imp ( nr_seq_procedimento_p bigint, nr_seq_material_p bigint) RETURNS bigint AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o código de serviço de pedidos de autorização ou complemento de autorização
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_servico_w		integer;
cd_retorno_w		integer;


BEGIN
if (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '') then
	begin
		select	cd_servico
		into STRICT		cd_servico_w
		from		ptu_pedido_aut_servico
		where		nr_seq_guia_proc	= nr_seq_procedimento_p;
	exception
	when others then
		cd_servico_w	:= null;
	end;

	if (coalesce(cd_servico_w::text, '') = '') then
			begin
				select	cd_servico
				into STRICT		cd_servico_w
				from		ptu_pedido_compl_aut_serv
				where		nr_seq_guia_proc	= nr_seq_procedimento_p;
			exception
			when others then
				cd_servico_w	:= null;
			end;
		end if;
elsif (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
	begin
		select	cd_servico
		into STRICT		cd_servico_w
		from		ptu_pedido_aut_servico
		where		nr_seq_guia_mat	= nr_seq_material_p;
	exception
	when others then
		cd_servico_w	:= null;
	end;

	if (coalesce(cd_servico_w::text, '') = '') then
		begin
			select	cd_servico
			into STRICT		cd_servico_w
			from		ptu_pedido_compl_aut_serv
			where		nr_seq_guia_mat	= nr_seq_material_p;
		exception
		when others then
			cd_servico_w 	:= null;
		end;
	end if;
end if;

return	cd_servico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
