SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_cod_especialidade ( nr_seq_proc_mat_p bigint, ie_tipo_item_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_cbo_saude_w		bigint;
cd_especialidade_w		smallint;
qt_registro_w			bigint := 0;


BEGIN
if	((ie_tipo_item_p	= 'P')	or (ie_tipo_item_p		= 'R')) then
	select	max(cd_especialidade)
	into STRICT	cd_especialidade_w
	from	ptu_nota_servico
	where	nr_seq_conta_proc	= nr_seq_proc_mat_p;
elsif (ie_tipo_item_p	= 'M') then
	select	max(cd_especialidade)
	into STRICT	cd_especialidade_w
	from	ptu_nota_servico
	where	nr_seq_conta_mat	= nr_seq_proc_mat_p;
end if;

if (cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '') then
	select	count(1)
	into STRICT	qt_registro_w
	from	especialidade_medica
	where	cd_ptu = cd_especialidade_w;

	select	max(nr_seq_cbo_saude)
	into STRICT	nr_seq_cbo_saude_w
	from	especialidade_medica
	where	cd_ptu		= cd_especialidade_w;
end if;
return	nr_seq_cbo_saude_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_cod_especialidade ( nr_seq_proc_mat_p bigint, ie_tipo_item_p text) FROM PUBLIC;

