DECLARE

 
cd_especialidade_externo_w	varchar(20);

BEGIN
 
 
select	max(a.cd_especialidade_externo) 
into STRICT	cd_especialidade_externo_w 
from	especialidade_medica	a, 
	medico_especialidade	b 
where	a.cd_especialidade	= b.cd_especialidade 
