SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_cod_integracao_gps ( cd_informado_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
E - Estado Civil
E - Motivo exclusão
*/
ds_retorno_w		varchar(10);


BEGIN

if (cd_informado_p IS NOT NULL AND cd_informado_p::text <> '') then
	if (ie_opcao_p	= 'E') then
		--Solteiro
		if (cd_informado_p	= '1') then
			ds_retorno_w	:= '1';
		--Casado;Concubinado
		elsif (cd_informado_p	in ('2','7')) then
			ds_retorno_w	:= '2';
		--Divorciado
		elsif (cd_informado_p	= '3') then
			ds_retorno_w	:= '5';
		--Viúvo
		elsif (cd_informado_p	= '5') then
			ds_retorno_w	:= '3';
		--Separado;Desquitado
		elsif (cd_informado_p	in ('4','6')) then
			ds_retorno_w	:= '4';
		--Outros
		elsif (cd_informado_p	= '9') then
			ds_retorno_w	:= '9';
		end if;
	elsif (ie_opcao_p	= 'M') then
		select	max(cd_intercambio)
		into STRICT	ds_retorno_w
		from	pls_motivo_cancelamento
		where	nr_sequencia	= cd_informado_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
