CREATE OR REPLACE FUNCTION pls_obter_cod_mat_conta (nr_seq_conta_mat_p bigint, cd_material_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(30)	:= null;


BEGIN
if (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then
	select	max(nr_seq_material)
	into STRICT	ds_retorno_w
	from	pls_conta_mat
	where	nr_sequencia	= nr_seq_conta_mat_p;
elsif (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_conta_mat
	where	nr_seq_material	= cd_material_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
