SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_cod_pessoa_atend ( nr_seq_localizador_p text, ie_tipo_pessoa_p text) RETURNS varchar AS $body$
DECLARE


/*

B       - Beneficiário;
BI     - Beneficiário de intercambio
P        - Pessoa física;
PG     - Pagador;
E       - Estipulante / Contratante;
PR     - Prestador;

*/
cd_retorno_pf_pj_w	varchar(30);
ie_tipo_pf_pj_w		varchar(2);


BEGIN

if (ie_tipo_pessoa_p = 'B') or (ie_tipo_pessoa_p = 'BI') then
	select	cd_pessoa_fisica,
		'PF'
	into STRICT	cd_retorno_pf_pj_w,
		ie_tipo_pf_pj_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_localizador_p;

elsif (ie_tipo_pessoa_p = 'PG') then
	select	CASE WHEN coalesce(cd_pessoa_fisica::text, '') = '' THEN cd_cgc  ELSE cd_pessoa_fisica END ,
		CASE WHEN coalesce(cd_pessoa_fisica::text, '') = '' THEN 'PJ'  ELSE 'PF' END
	into STRICT	cd_retorno_pf_pj_w,
		ie_tipo_pf_pj_w
	from	pls_contrato_pagador
	where	nr_sequencia = nr_seq_localizador_p;

elsif (ie_tipo_pessoa_p = 'E') then
	select	CASE WHEN coalesce(cd_pf_estipulante::text, '') = '' THEN cd_cgc_estipulante  ELSE cd_pf_estipulante END ,
		CASE WHEN coalesce(cd_pf_estipulante::text, '') = '' THEN 'PJ'  ELSE 'PF' END
	into STRICT	cd_retorno_pf_pj_w,
		ie_tipo_pf_pj_w
	from	pls_contrato
	where	nr_sequencia = nr_seq_localizador_p;

elsif (ie_tipo_pessoa_p = 'PR') then
	select	CASE WHEN coalesce(cd_pessoa_fisica::text, '') = '' THEN cd_cgc  ELSE cd_pessoa_fisica END ,
		CASE WHEN coalesce(cd_pessoa_fisica::text, '') = '' THEN 'PJ'  ELSE 'PF' END
	into STRICT	cd_retorno_pf_pj_w,
		ie_tipo_pf_pj_w
	from	pls_prestador
	where	nr_sequencia = nr_seq_localizador_p;
end if;

return	cd_retorno_pf_pj_w || ',' || ie_tipo_pf_pj_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_cod_pessoa_atend ( nr_seq_localizador_p text, ie_tipo_pessoa_p text) FROM PUBLIC;

