CREATE OR REPLACE FUNCTION pls_obter_cod_prest_estab ( nr_seq_prestador_p pls_prestador.nr_sequencia%type, cd_prestador_p pls_prestador.cd_prestador%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ie_controla_stab_p pls_controle_estab.ie_prestador%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(30)	:= null;
qt_prestador_w		bigint;


BEGIN

if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then
	select	max(cd_prestador)
	into STRICT	ds_retorno_w
	from	pls_prestador
	where	nr_sequencia	= nr_seq_prestador_p
	and	ie_situacao	= 'A';

	if (coalesce(ds_retorno_w,'0') = '0') then
		select	max(cd_prestador)
		into STRICT	ds_retorno_w
		from	pls_prestador
		where	nr_sequencia	= nr_seq_prestador_p;
	end if;
elsif (cd_prestador_p IS NOT NULL AND cd_prestador_p::text <> '') then

	select	max(t.nr_sequencia)
	into STRICT	ds_retorno_w
	from (	SELECT	nr_sequencia
		from	pls_prestador
		where	cd_prestador		= upper(cd_prestador_p)
		and	ie_situacao		= 'A'
		and	ie_controla_stab_p	= 'N'
		
union all

		-- caso tenha informado o estabelecimento
		SELECT	nr_sequencia
		from	pls_prestador
		where	cd_prestador		= upper(cd_prestador_p)
		and	ie_situacao		= 'A'
		and	ie_controla_stab_p	= 'S'
		and	(cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '')
		and	cd_estabelecimento	= cd_estabelecimento_p) t;

	if (coalesce(ds_retorno_w,'0') = '0') then
		select	max(t.nr_sequencia)
		into STRICT	ds_retorno_w
		from (	SELECT	nr_sequencia
			from	pls_prestador
			where	cd_prestador		= upper(cd_prestador_p)
			and	ie_controla_stab_p	= 'N'
			
union all

			SELECT	nr_sequencia
			from	pls_prestador
			where	cd_prestador		= upper(cd_prestador_p)
			and	ie_controla_stab_p	= 'S'
			and	(cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '')
			and	cd_estabelecimento	= cd_estabelecimento_p) t;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
