CREATE OR REPLACE FUNCTION pls_obter_comp_repasse ( dt_referencia_p pls_segurado_repasse.dt_repasse%type, dt_mes_compet_prot_p pls_protocolo_conta.dt_mes_competencia%type, nr_seq_segurado_p pls_segurado_repasse.nr_seq_segurado%type, nr_seq_congenere_p pls_segurado_repasse.nr_seq_congenere_atend%type, ie_opcao_p pls_segurado_repasse.ie_tipo_repasse%type) RETURNS varchar AS $body$
DECLARE

					
ie_tipo_compartilhamento_w		pls_segurado_repasse.ie_tipo_compartilhamento%type;
ie_tipo_repasse_w			pls_segurado_repasse.ie_tipo_repasse%type;
dt_repasse_w				pls_segurado_repasse.dt_repasse%type;
dt_fim_repasse_w			pls_segurado_repasse.dt_fim_repasse%type;
ie_data_tipo_segurado_w			pls_parametros.ie_data_tipo_segurado%type;
dt_referencia_w				pls_protocolo_conta.dt_mes_competencia%type;


BEGIN

select	coalesce(max(a.ie_data_tipo_segurado),'A')
into STRICT	ie_data_tipo_segurado_w
from	pls_parametros a
where	a.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

if (ie_data_tipo_segurado_w = 'A') then
	dt_referencia_w	:= dt_referencia_p;
else
	dt_referencia_w	:= dt_mes_compet_prot_p;
end if;

SELECT * FROM pls_obter_dados_repasse(	dt_referencia_w, nr_seq_segurado_p, nr_seq_congenere_p, ie_tipo_repasse_w, ie_tipo_compartilhamento_w, dt_repasse_w, dt_fim_repasse_w) INTO STRICT ie_tipo_repasse_w, ie_tipo_compartilhamento_w, dt_repasse_w, dt_fim_repasse_w;
				
if (ie_opcao_p = 'C') then
	return ie_tipo_compartilhamento_w;
end if;

if (ie_opcao_p = 'R') then
	return ie_tipo_repasse_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
