CREATE OR REPLACE FUNCTION pls_obter_condicao_evento_comb ( nr_seq_validacao_p bigint, ie_tipo_consulta_p text) RETURNS varchar AS $body$
DECLARE


ie_consistencia_conta_w		varchar(1);
ie_imp_xml_w			varchar(1);
ie_imp_pre_analise_w		varchar(1);
ie_consistencia_reembolso_w	varchar(1);
ie_portal_web_w			varchar(1);


BEGIN
if (nr_seq_validacao_p IS NOT NULL AND nr_seq_validacao_p::text <> '') then
	begin
	select	ie_consistencia_conta,
		ie_imp_xml,
		ie_imp_pre_analise,
		ie_consistencia_reembolso,
		ie_portal_web
	into STRICT	ie_consistencia_conta_w,
		ie_imp_xml_w,
		ie_imp_pre_analise_w,
		ie_consistencia_reembolso_w,
		ie_portal_web_w
	from pls_oc_cta_tipo_validacao
	where nr_sequencia = nr_seq_validacao_p;
	end;

	if 	ie_tipo_consulta_p = 'CC' then
		return coalesce(ie_consistencia_conta_w,'N');

	elsif 	ie_tipo_consulta_p = 'IMP' then
		return coalesce(ie_imp_xml_w,'N');

	elsif 	ie_tipo_consulta_p = 'PTU' then
		return coalesce(ie_imp_pre_analise_w,'N');

	elsif 	ie_tipo_consulta_p = 'PW' then
		return coalesce(ie_portal_web_w,'N');

	elsif 	ie_tipo_consulta_p = 'RE' then
		return coalesce(ie_consistencia_reembolso_w,'N');
	else 	return 'N';

	end if;
else 	return 'N';
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
