CREATE OR REPLACE FUNCTION pls_obter_congenere_interc ( nr_seq_intercambio_p bigint) RETURNS varchar AS $body$
DECLARE


nm_congenere_w			varchar(255);

BEGIN
select	substr(CASE WHEN substr(pls_obter_nome_congenere(nr_seq_congenere),1,255)='' THEN 	substr(pls_obter_nome_congenere(nr_seq_oper_congenere),1,255)  ELSE substr(pls_obter_nome_congenere(nr_seq_congenere),1,255) END ,1,255)
into STRICT	nm_congenere_w
from	pls_intercambio
where	nr_sequencia	= nr_seq_intercambio_p;
return	nm_congenere_w;

end;
