CREATE OR REPLACE FUNCTION pls_obter_conselho_tiss_xml ( sg_conselho_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o codigo TISS do conselho que e enviado no XML
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(255);


BEGIN
if (sg_conselho_p IS NOT NULL AND sg_conselho_p::text <> '') then
	begin
	--select	decode(sg_conselho_p,'CRAS','1','COREN','2','CRF','3','CRFA','4','CREFITO','5','CRM','6','CRN','7','CRO','8','CRP','9','OUT','10')
	select	CASE WHEN sg_conselho_p='CRAS' THEN '1' WHEN sg_conselho_p='CRESS' THEN 1 WHEN sg_conselho_p='COREN' THEN '2' WHEN sg_conselho_p='CRF' THEN '3' WHEN sg_conselho_p='CRFA' THEN '4' WHEN sg_conselho_p='CREFONO' THEN 4 WHEN sg_conselho_p='CREFITO' THEN '5' WHEN sg_conselho_p='CRM' THEN '6' WHEN sg_conselho_p='CRN' THEN '7' WHEN sg_conselho_p='CRO' THEN '8' WHEN sg_conselho_p='CRP' THEN '9' WHEN sg_conselho_p='OUT' THEN '10' END
	into STRICT	ds_retorno_w
	;
	exception
	when others then
		ds_retorno_w := null;
	end;
end if;

return	ds_retorno_w;

end;
$body$
