CREATE OR REPLACE FUNCTION pls_obter_contas_analises_fec ( nr_seq_analise_p bigint, cd_guia_p text) RETURNS varchar AS $body$
DECLARE

				 
/*Esta function funciona especificamente para obter se todas as contas de analises na tela da função 
  OPS - Análise de Contas Médicas estão fechadas 
   
  N - Todas estão fechadas 
  S - Existe contas abertas*/
				 
				 
ds_retorno_w			varchar(1) := 'S';


BEGIN 
 
select	CASE WHEN count(a.nr_sequencia)=0 THEN  'N'  ELSE 'S' END  
into STRICT	ds_retorno_w 
from	pls_resumo_conta_v a, 
	pls_conta b 
where	a.nr_seq_conta 	  	= b.nr_sequencia 
and	a.nr_seq_analise	= nr_seq_analise_p 
and	coalesce(a.nr_seq_conta,0) > 0 
and	b.ie_status <> 'F';
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
