CREATE OR REPLACE FUNCTION pls_obter_conta_grau_partic ( nr_seq_conta_p bigint, nr_seq_item_p bigint, nr_seq_grau_partic_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';


BEGIN

if (coalesce(nr_seq_item_p,0) > 0) then

	select	CASE WHEN count(a.nr_sequencia)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	FROM pls_conta b, pls_conta_proc a
LEFT OUTER JOIN pls_proc_participante c ON (a.nr_sequencia = c.nr_seq_conta_proc)
WHERE a.nr_seq_conta 		= b.nr_sequencia  and ((c.nr_seq_grau_partic	= nr_seq_grau_partic_p) or (b.nr_seq_grau_partic	= nr_seq_grau_partic_p)) and a.nr_sequencia		= nr_seq_item_p;

else
	select	CASE WHEN count(a.nr_sequencia)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	FROM pls_conta b, pls_conta_proc a
LEFT OUTER JOIN pls_proc_participante c ON (a.nr_sequencia = c.nr_seq_conta_proc)
WHERE a.nr_seq_conta 		= b.nr_sequencia  and ((c.nr_seq_grau_partic	= nr_seq_grau_partic_p) or (b.nr_seq_grau_partic	= nr_seq_grau_partic_p)) and a.nr_seq_conta		= nr_seq_conta_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
