CREATE OR REPLACE FUNCTION pls_obter_conta_imposto ( ie_debito_credito_p text, nr_seq_mensalidade_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(20);
ie_tipo_pagador_w	varchar(2);
dt_referencia_w		timestamp;


BEGIN

select	CASE WHEN coalesce(b.cd_pessoa_fisica::text, '') = '' THEN 'PJ'  ELSE 'PF' END ,
	a.dt_referencia
into STRICT	ie_tipo_pagador_w,
	dt_referencia_w
from	pls_mensalidade		a,
	pls_contrato_pagador	b
where	a.nr_seq_pagador	= b.nr_sequencia
and	a.nr_sequencia		= nr_seq_mensalidade_p;


select	max(cd_conta_contabil)
into STRICT	ds_retorno_w
from	pls_conta_contabil_imposto
where	cd_estabelecimento	= cd_estabelecimento_p
and	trunc(dt_referencia_w,'dd') between trunc(dt_vigencia_inicial,'dd') and trunc(coalesce(dt_vigencia_final,dt_referencia_w),'dd')
and	coalesce(ie_debito_credito,'C') = ie_debito_credito_p
and	((ie_tipo_pessoa = ie_tipo_pagador_w) or (ie_tipo_pessoa = 'A'));

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
