CREATE OR REPLACE FUNCTION pls_obter_conta_item_auditoria ( nr_seq_conta_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1)	:= 'N';
qt_registro_1_w			bigint;
qt_registro_2_w			bigint;
qt_registro_w			bigint;
ie_origem_conta_w		varchar(3);

qt_registro_pre_w		bigint;


BEGIN

begin
select	ie_origem_conta
into STRICT	ie_origem_conta_w
from	pls_conta
where	nr_sequencia = nr_seq_conta_p;
exception
when others then
	ie_origem_conta_w	:= '';
end;

ie_origem_conta_w	:= coalesce(ie_origem_conta_w,'0');

select	count(1)
into STRICT	qt_registro_1_w
from	pls_ocorrencia		b,
	pls_ocorrencia_benef	a
where	a.nr_seq_conta		= nr_seq_conta_p
and	a.nr_seq_ocorrencia	= b.nr_sequencia
and	b.ie_auditoria_conta	= 'S';

select	count(1)
into STRICT	qt_registro_pre_w
from	pls_ocorrencia		b,
	pls_ocorrencia_benef	a
where	a.nr_seq_conta		= nr_seq_conta_p
and	a.nr_seq_ocorrencia	= b.nr_sequencia
and	b.ie_auditoria_conta	= 'S'
and (b.ie_pre_analise = 'S');

select	count(1)
into STRICT	qt_registro_2_w
from	pls_ocorrencia		b,
	pls_ocorrencia_benef	a
where	a.nr_seq_conta		= nr_seq_conta_p
and	a.nr_seq_ocorrencia	= b.nr_sequencia
and	exists (SELECT	1	/*Diego OS 331561*/
		from	pls_ocorrencia_grupo x
		where	x.nr_seq_ocorrencia				= b.nr_sequencia
		and (x.ie_conta_medica = 'S' or coalesce(x.ie_conta_medica::text, '') = '')
		and (x.ie_origem_conta = ie_origem_conta_w or coalesce(x.ie_origem_conta::text, '') = '')
		and	x.ie_situacao = 'A'
		);

qt_registro_w	:= coalesce(qt_registro_1_w,0) + coalesce(qt_registro_2_w,0);

if (qt_registro_w	> 0) then
	ie_retorno_w	:= 'S';
end if;

if (coalesce(qt_registro_pre_w,0) = qt_registro_w) and (ie_origem_conta_w <> 'A')then
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_conta_item_auditoria ( nr_seq_conta_p bigint ) FROM PUBLIC;

