CREATE OR REPLACE FUNCTION pls_obter_conta_pos_auditoria ( nr_seq_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_origem_conta_w		varchar(3);
ie_retorno_w			varchar(1)	:= 'N';
qt_registro_w			bigint;
nr_seq_conta_pos_estab_w	bigint;
qt_registro_1_w			bigint;
qt_registro_2_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_conta_pos_estabelecido	a,
		pls_conta_proc			b
	where	a.nr_seq_conta_proc	= b.nr_sequencia
	and	b.nr_seq_conta		= a.nr_seq_conta
	and	b.nr_seq_conta		= nr_seq_conta_p
	and	((a.ie_situacao		= 'A') or (coalesce(a.ie_situacao::text, '') = ''))
	
union

	SELECT	a.nr_sequencia
	from	pls_conta_pos_estabelecido	a,
		pls_conta_mat			b
	where	a.nr_seq_conta_mat	= b.nr_sequencia
	and	b.nr_seq_conta		= a.nr_seq_conta
	and	b.nr_seq_conta		= nr_seq_conta_p
	and	((a.ie_situacao		= 'A') or (coalesce(a.ie_situacao::text, '') = ''))
	order by 1;


BEGIN
select	max(ie_origem_conta)
into STRICT	ie_origem_conta_w
from	pls_conta
where	nr_sequencia = nr_seq_conta_p;

open C01;
loop
fetch C01 into	
	nr_seq_conta_pos_estab_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(1)
	into STRICT	qt_registro_1_w
	from	pls_ocorrencia_benef	a,
		pls_ocorrencia		b
	where	a.nr_seq_ocorrencia		= b.nr_sequencia
	and	a.nr_seq_conta_pos_estab	= nr_seq_conta_pos_estab_w
	and	b.ie_auditoria_conta		= 'S'  LIMIT 1;
	
	select	count(1)
	into STRICT	qt_registro_2_w
	from	pls_ocorrencia_benef	a,
		pls_ocorrencia		b
	where	a.nr_seq_ocorrencia		= b.nr_sequencia
	and	a.nr_seq_conta_pos_estab	= nr_seq_conta_pos_estab_w
	and	exists (	SELECT	1 /*Diego OS 331561*/
				from	pls_ocorrencia_grupo x
				where	x.nr_seq_ocorrencia 				= b.nr_sequencia
				and	coalesce(x.ie_conta_medica,'S') 			= 'S'
				and	x.ie_situacao					= 'A'
				and	coalesce(x.ie_origem_conta,ie_origem_conta_w)	= ie_origem_conta_w)  LIMIT 1;

	qt_registro_w	:= coalesce(qt_registro_1_w,0) + coalesce(qt_registro_2_w,0);
				
	if (qt_registro_w	> 0) then		
		ie_retorno_w	:= 'S';
		goto Final;
	end if;
	
	end;
end loop;
close C01;

<<Final>>

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
