SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_conta_princ_atend ( nr_seq_conta_p pls_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type ) RETURNS PLS_CONTA.NR_SEQUENCIA%TYPE AS $body$
DECLARE


cd_guia_ref_w			pls_conta.cd_guia_referencia%type;
ie_tipo_guia_w			pls_conta.ie_tipo_guia%type;
nr_retorno_w			pls_conta.nr_sequencia%type;
nr_seq_segurado_w		pls_segurado.nr_sequencia%type;
cd_atendimento_w		pls_tipo_atendimento.cd_tiss%type;
ie_regime_atendimento_w	pls_conta.ie_regime_atendimento%type;


BEGIN

/*Obter os dados da conta*/

select	cd_guia_referencia,
	nr_seq_segurado,	
	ie_tipo_guia,
	cd_tiss_atendimento,
	ie_regime_atendimento
into STRICT	cd_guia_ref_w,
	nr_seq_segurado_w,	
	ie_tipo_guia_w,
	cd_atendimento_w,
	ie_regime_atendimento_w
from	pls_conta_v
where	nr_sequencia = nr_seq_conta_p;

-- Se for uma guia de honorario individual entao deve ser buscado a primeira conta de guia de resumo de internacao cadastrada referente a esta guia.
if (ie_tipo_guia_w = 6) then

	select	min(a.nr_sequencia)
	into STRICT	nr_retorno_w
	from	pls_conta a
	where	a.cd_guia_referencia	= cd_guia_ref_w
	and 	a.ie_tipo_guia 		= 5
	and	a.nr_seq_segurado	= nr_seq_segurado_w;	
-- Se for uma guia de SP\SADT deve ser verificado o tipo de atendimento. Se for uma conta de SP\SADT gerada dentro de uma guia de internacao, deve

-- ser buscado a primeira conta cadastrada de resumo de internacao para esta guia que esta sendo referenciada. Se for uma conta de SP\SADT que nao estiver

-- em um atendimento de internacao, deve ser buscado a primeira conta de SP\SADT cadastrada para este atendimento.
elsif (ie_tipo_guia_w = 4) then

	-- SADT internado.
	if (cd_atendimento_w = '07' or ie_regime_atendimento_w = '03') then
	
		select	min(a.nr_sequencia)
		into STRICT	nr_retorno_w
		from	pls_conta	a
		where	a.nr_seq_segurado	= nr_seq_segurado_w
		and	a.cd_guia_referencia	= cd_guia_ref_w		
		and	a.ie_tipo_guia		= 5;
		
		if (coalesce(nr_retorno_w::text, '') = '') then
			select	min(a.nr_sequencia)
			into STRICT	nr_retorno_w
			from	pls_conta	a
			where	a.nr_seq_segurado	= nr_seq_segurado_w
			and	a.cd_guia_ok		= cd_guia_ref_w		
			and	a.ie_tipo_guia		= 5;
		end if;
	else	
		select	min(a.nr_sequencia)
		into STRICT	nr_retorno_w
		from	pls_conta	a
		where	a.nr_seq_segurado	= nr_seq_segurado_w
		and	a.cd_guia		= cd_guia_ref_w
		and	a.ie_tipo_guia		= 4;	
		
		if (coalesce(nr_retorno_w::text, '') = '') then
			select	min(a.nr_sequencia)
			into STRICT	nr_retorno_w
			from	pls_conta	a
			where	a.nr_seq_segurado	= nr_seq_segurado_w
			and	a.cd_guia_ok		= cd_guia_ref_w
			and	a.ie_tipo_guia		= 4;	
		end if;
	end if;
end if;

-- Se nao foi encontrado um conta principaal para esta conta entao assume-se que a mesma e a unica conta do atendimento, sendo tambem a principal conta do mesmo.
if (coalesce(nr_retorno_w::text, '') = '') then
	nr_retorno_w := nr_seq_conta_p;
end if;

return nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
