CREATE OR REPLACE FUNCTION pls_obter_conta_rec_glosa_mens ( nr_seq_mensalidade_seg_p pls_mensalidade_segurado.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, ie_tipo_item_p pls_mensalidade_seg_item.ie_tipo_item%type) RETURNS bigint AS $body$
DECLARE

 
nr_seq_conta_rec_w	pls_rec_glosa_conta.nr_sequencia%type;


BEGIN 
 
if (ie_tipo_item_p = '3') then 
	select	max(nr_seq_conta_rec) 
	into STRICT	nr_seq_conta_rec_w 
	from (SELECT c.nr_sequencia nr_seq_conta_rec 
		from  pls_conta_coparticipacao a, 
			pls_rec_glosa_proc b, 
			pls_rec_glosa_conta c 
		where  b.nr_sequencia = a.nr_seq_proc_rec 
		and   c.nr_sequencia = b.nr_seq_conta_rec 
		and   a.nr_seq_conta = nr_seq_conta_p 
		and   a.nr_seq_mensalidade_seg = nr_seq_mensalidade_seg_p 
		
union all
 
		SELECT c.nr_sequencia nr_seq_conta_rec 
		from  pls_conta_coparticipacao a, 
			pls_rec_glosa_mat b, 
			pls_rec_glosa_conta c 
		where  b.nr_sequencia = a.nr_seq_mat_rec 
		and   c.nr_sequencia = b.nr_seq_conta_rec 
		and   a.nr_seq_conta = nr_seq_conta_p 
		and   a.nr_seq_mensalidade_seg = nr_seq_mensalidade_seg_p) alias2;
elsif (ie_tipo_item_p in ('6','7')) then 
	select	max(nr_seq_conta_rec) 
	into STRICT	nr_seq_conta_rec_w 
	from (SELECT	c.nr_sequencia nr_seq_conta_rec 
		from  pls_conta_pos_estabelecido a, 
			pls_rec_glosa_proc b, 
			pls_rec_glosa_conta c 
		where  b.nr_sequencia = a.nr_seq_proc_rec 
		and   c.nr_sequencia = b.nr_seq_conta_rec 
		and   a.nr_seq_conta = nr_seq_conta_p 
		and   a.nr_seq_mensalidade_seg = nr_seq_mensalidade_seg_p 
		
union all
 
		SELECT c.nr_sequencia nr_seq_conta_rec 
		from  pls_conta_pos_estabelecido a, 
			pls_rec_glosa_mat b, 
			pls_rec_glosa_conta c 
		where  b.nr_sequencia = a.nr_seq_mat_rec 
		and   c.nr_sequencia = b.nr_seq_conta_rec 
		and   a.nr_seq_conta = nr_seq_conta_p 
		and   a.nr_seq_mensalidade_seg = nr_seq_mensalidade_seg_p) alias3;
	 
	if (coalesce(nr_seq_conta_rec_w::text, '') = '') then 
		select	max(nr_seq_conta_rec) 
		into STRICT	nr_seq_conta_rec_w 
		from (SELECT	c.nr_seq_conta_rec nr_seq_conta_rec 
			from	pls_mensalidade_seg_item a, 
				pls_mensalidade_item_conta b, 
				pls_conta_pos_proc c 
			where	a.nr_sequencia = b.nr_seq_item 
			and	c.nr_sequencia = b.nr_seq_pos_proc 
			and	a.nr_seq_mensalidade_seg = nr_seq_mensalidade_seg_p 
			and	a.nr_seq_conta = nr_seq_conta_p 
			
union all
 
			SELECT	c.nr_seq_conta_rec nr_seq_conta_rec 
			from	pls_mensalidade_seg_item a, 
				pls_mensalidade_item_conta b, 
				pls_conta_pos_mat c 
			where	a.nr_sequencia = b.nr_seq_item 
			and	c.nr_sequencia = b.nr_seq_pos_mat 
			and	a.nr_seq_mensalidade_seg = nr_seq_mensalidade_seg_p 
			and	a.nr_seq_conta = nr_seq_conta_p) alias3;
	end if;
end if;
 
return	nr_seq_conta_rec_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
