CREATE OR REPLACE FUNCTION pls_obter_conta_titular (nr_seq_segurado_p pls_segurado.nr_sequencia%type, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
cd_pessoa_fisica_conta_w	pessoa_fisica_conta.nr_sequencia%type;	
retorno_w			varchar(50);

/*	ie_tipo
	CB = Codigo do banco
	CA = Número da Agencia bancária
	NC = Numero da conta corrente
*/
BEGIN

select 	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	pls_segurado
where	nr_sequencia = nr_seq_segurado_p;

select  max(nr_sequencia)
into STRICT    cd_pessoa_fisica_conta_w
from	pessoa_fisica_conta	a,
	pessoa_fisica		b
where 	b.cd_pessoa_fisica 	= cd_pessoa_fisica_w
and	a.cd_pessoa_fisica	= b.cd_pessoa_fisica;

if (ie_tipo_p = 'CB') then
	
	select 	x.cd_banco
	into STRICT 	retorno_w
	from 	pessoa_fisica_conta x
	where	x.nr_sequencia	= cd_pessoa_fisica_conta_w;
	
elsif (ie_tipo_p = 'CA') then
	
	select 	x.cd_agencia_bancaria || x.ie_digito_agencia
	into STRICT 	retorno_w
	from 	pessoa_fisica_conta x
	where	x.nr_sequencia	= cd_pessoa_fisica_conta_w;
	
elsif (ie_tipo_p = 'NC') then

	select 	x.nr_conta || x.nr_digito_conta
	into STRICT 	retorno_w
	from 	pessoa_fisica_conta x
	where	x.nr_sequencia	= cd_pessoa_fisica_conta_w;
	
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_conta_titular (nr_seq_segurado_p pls_segurado.nr_sequencia%type, ie_tipo_p text) FROM PUBLIC;

