SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_contratado_cooperado ( nr_seq_guia_p bigint, cd_pessoa_fisica_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

				 
/* 
ie_tipo_p 
C	= CÃ³digo 
N 	= Nome 
CN	= CNES 
*/
 
 
ie_cooperado_w			varchar(2);
cd_pf_pj_w			varchar(255);
nm_prestador_w			varchar(255);
ds_retorno_w			varchar(255);
nr_seq_cooperado_w		bigint;
cd_cnes_w			varchar(20);
nr_seq_guia_w			bigint;
ie_tipo_guia_w			varchar(255);


BEGIN 
 
ie_cooperado_w	:= pls_obter_se_cooperado(cd_pessoa_fisica_p,'');
 
if (ie_cooperado_w	= 'S') then 
	begin 
		select	coalesce(max(nr_sequencia),0) 
		into STRICT	nr_seq_cooperado_w 
		from	pls_cooperado 
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	exception 
	when others then 
		nr_seq_cooperado_w	:= 0;
	end;
 
	if (nr_seq_cooperado_w	<> 0) then 
		if (ie_tipo_p	= 'C') then 
			ds_retorno_w	:= cd_pessoa_fisica_p;
		elsif (ie_tipo_p	= 'N') then 
			select	obter_nome_pf_pj(a.cd_pessoa_fisica,a.cd_cgc) 
			into STRICT	ds_retorno_w 
			from	pls_cooperado a 
			where	a.nr_sequencia	= nr_seq_cooperado_w;
		elsif (ie_tipo_p	= 'CN') then 
			select	cd_cnes 
			into STRICT	ds_retorno_w 
			from	pessoa_fisica 
			where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
		end if;
	end if;
elsif (ie_cooperado_w	= 'N') then 
	begin 
		select	ie_tipo_guia 
		into STRICT	ie_tipo_guia_w 
		from	pls_guia_plano 
		where	nr_sequencia	= nr_seq_guia_p;
	exception 
	when others then 
		ie_tipo_guia_w	:= 'X';
	end;
	 
	if (coalesce(ie_tipo_guia_w,'X')	= '8') then 
		begin 
			select	max(coalesce(nr_seq_guia_principal,cd_guia_principal)) 
			into STRICT	nr_seq_guia_w 
			from	pls_guia_plano 
			where	nr_sequencia	= nr_seq_guia_p;
		exception 
		when others then 
			nr_seq_guia_w	:= 0;
		end;
	else 
		nr_seq_guia_w	:= nr_seq_guia_p;
	end if;
	 
	begin 
		select	CASE WHEN b.cd_pessoa_fisica='' THEN b.cd_cgc  ELSE b.cd_pessoa_fisica END , 
			substr(obter_nome_pf_pj(b.cd_pessoa_fisica, b.cd_cgc),1,254), 
			substr(pls_obter_cnes_prestador(b.nr_sequencia),1,40) 
		into STRICT	cd_pf_pj_w, 
			nm_prestador_w, 
			cd_cnes_w 
		from	pls_guia_plano a, 
			pls_prestador b 
		where	a.nr_seq_prestador	= b.nr_sequencia 
		and	a.nr_sequencia		= nr_seq_guia_w;
	exception 
	when others then 
		cd_pf_pj_w	:= '';
		nm_prestador_w	:= '';
		cd_cnes_w	:= '';
	end;
	 
	if (ie_tipo_p	= 'C') then 
		ds_retorno_w	:= cd_pf_pj_w;
	elsif (ie_tipo_p	= 'N') then 
		ds_retorno_w	:= nm_prestador_w;
	elsif (ie_tipo_p	= 'CN') then 
		ds_retorno_w	:= cd_cnes_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
