CREATE OR REPLACE FUNCTION pls_obter_contratos_empresa ( nr_seq_empresa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_contratos_w		varchar(2000);
nr_contrato_w		bigint;

C01 CURSOR FOR
		SELECT	a.nr_contrato
		from	pls_contrato				a,
				pls_desc_empresa			b,
				pls_contrato_pagador		c,
				pls_contrato_pagador_fin	d
		where	b.nr_sequencia		= d.nr_seq_empresa
		and		d.nr_seq_pagador 	= c.nr_sequencia
		and		c.nr_seq_contrato	= a.nr_sequencia
		and		b.nr_sequencia		= nr_seq_empresa_p
		and		a.ie_situacao		= 2
		and		coalesce(d.dt_fim_vigencia::text, '') = '';

BEGIN

open C01;
loop
fetch C01 into
		nr_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_contratos_w := ds_contratos_w || nr_contrato_w || ',';
		end;
end loop;
close C01;

ds_contratos_w := substr(ds_contratos_w,1,length(ds_contratos_w) - 1);
return	ds_contratos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
